//Title: BAH_MosaicLoader
//Description: Looks for attempts to create Windows Defender exclusions through registry value keys being set in various different methods (e.g., PowerShell, Registry key value modifications, and via Defender Security Panel. Catches on exclusion paths, extensions, and processes.
//Author: Steven Wan
//False Positives: None applicable at this time.

let signInLookup = (
SigninLogs
| where TimeGenerated >= ago(1d)
| where DeviceDetail contains "displayName"
| extend DeviceDetailJson = parse_json(DeviceDetail)
| extend DeviceNameSI = tostring(DeviceDetailJson.displayName)
| distinct DeviceNameSI, UserPrincipalName, UserDisplayName
);
DeviceRegistryEvents
| where TimeGenerated >= ago(1d)
| where ((ActionType == "RegistryValueSet") and (RegistryKey startswith @"HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\Windows Defender\Exclusions\Paths" 
  or RegistryKey startswith @"HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\Windows Defender\Exclusions\Extensions"
  or RegistryKey startswith @"HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\Windows Defender\Exclusions\Processes"))
| extend SILogDeviceName = toupper((split(DeviceName, "."))[0]) // for signin lookup by device will find last user signed into device if it was in the last 24 hours
| join kind=leftouter signInLookup on $left.SILogDeviceName == $right.DeviceNameSI // this join will add UserPrincipalName from here forward based on device name
| project TimeGenerated, InitiatingProcessAccountName, UserPrincipalName, ActionType,  DeviceId, DeviceName, RegistryKey, RegistryValueName
