//Title: BAH_ArbitraryOfficeFileDownloads
//Description: Detects potential arbitrary file download using a Microsoft Office application. This threat was discovered through the BazarCall malware utilized by call centers in late January 2022.
//Author: Steven Wan
//False Positives: None applicable at this time.

let BazarCallHashes = dynamic ([
    "2632c0cc222a6d436b50a418605a7bd4fa8f363ab8d93d10b831cdb28a2ac1bc", // SHA256 Hash for BazarLoader Malware
    "f3b5cf1e40aed4567a8996cf107285907d432b4bc8cc3d0b46aae628813d82d4", // BazarLoader DLL
	"db53f42e13d2685bd34dbc5c79fad637c9344e72e210ca05504420874e98c2a6" // SHA256 Hash for BazarLoader Malware
    ]);
let signInLookup = (
SigninLogs
| where TimeGenerated >= ago(1d)
| where DeviceDetail contains "displayName"
| extend DeviceDetailJson = parse_json(DeviceDetail)
| extend DeviceNameSI = tostring(DeviceDetailJson.displayName)
| distinct DeviceNameSI, UserPrincipalName, UserDisplayName
);
DeviceProcessEvents | where (((InitiatingProcessFolderPath endswith @"\EXCEL.EXE" or InitiatingProcessFolderPath endswith @"\POWERPNT.EXE" or InitiatingProcessFolderPath endswith @"\WINWORD.exe")) and (InitiatingProcessCommandLine contains "http://" or InitiatingProcessCommandLine contains "https://") and InitiatingProcessSHA256 has_any(BazarCallHashes))
| extend SILogDeviceName = toupper((split(DeviceName, "."))[0]) // for signin lookup by device will find last user signed into device if it was in the last 24 hours
| join kind=leftouter signInLookup on $left.SILogDeviceName == $right.DeviceNameSI // this join will add UserPrincipalName from here forward based on device name
| project TimeGenerated,SILogDeviceName,AccountName,ActionType,InitiatingProcessCommandLine,InitiatingProcessFolderPath,InitiatingProcessSHA256,ProcessCommandLine
