//Title: BAH_TamperProtectionDetection
//Description: Looks in the DeviceEvents table for any instance of 'TamperingAttempt' via ActionType. 
//This will ignore attempts from McAfee to disable MDE via mfetp.exe
//Authors: Steven Wan, Rob Russell, Michael Gibbs
//False Positives: None applicable at this time.
let signInLookup = (
SigninLogs
| where TimeGenerated >= ago(1d)
| where DeviceDetail contains "displayName"
| extend DeviceDetailJson = parse_json(DeviceDetail)
| extend DeviceNameSI = tostring(DeviceDetailJson.displayName)
| distinct DeviceNameSI, UserPrincipalName, UserDisplayName
);
let detectMcAfee = (
DeviceEvents
| where InitiatingProcessFileName == "mfetp.exe"
| project TimeGenerated, DeviceName, ProcessId, InitiatingProcessFileName
);
DeviceEvents
| where ActionType contains "TamperingAttempt"
| extend AdditionalFields = todynamic(AdditionalFields)
| extend TamperingAction = tostring(AdditionalFields.['TamperingAction'])
| extend Status = tostring(AdditionalFields.['Status'])
| extend Target = tostring(AdditionalFields.['Target'])
| extend TargetProperty = replace_regex(Target,@"(.*)\\(.*)",@"\2")
| extend OriginalValue = tostring(AdditionalFields.['OriginalValue'])
| extend TamperingAttemptedValue = tostring(AdditionalFields.['TamperingAttemptedValue'])
// For ThreatSeverityDefaultAction ignore changes made by GPO or Endpoint configuration manager (SCCM)
| where ( TargetProperty startswith "Disable" and TamperingAttemptedValue == 1 ) or ( ( Target contains "ThreatSeverityDefaultAction" ) and ( InitiatingProcessCommandLine !in ("svchost.exe -k GPSvcGroup","svchost.exe -k netsvcs -p -s gpsvc") and InitiatingProcessFolderPath != @"C:\Windows\System32" ) and ( InitiatingProcessCommandLine != "CcmExec.exe" and InitiatingProcessFolderPath == @"C:\Windows\CCM" ))
| where Status != "Ignored"
| extend TargetProperty = iff( ( TargetProperty matches regex @"\d" ), strcat("ThreatSeverityDefaultAction-",TargetProperty), TargetProperty)
| join kind=leftouter detectMcAfee on $left.InitiatingProcessParentId == $right.ProcessId, $left.DeviceName == $right.DeviceName
| where not (InitiatingProcessFileName1 == "mfetp.exe") // if McAfee tried to disable MDE ignore
| extend SILogDeviceName = toupper((split(DeviceName, "."))[0]) // for signin lookup by device will find last user signed into device if it was in the last 24 hours
| join kind=leftouter signInLookup on $left.SILogDeviceName == $right.DeviceNameSI // this join will add UserPrincipalName from here forward based on device name
| project TimeGenerated, DeviceName, TamperingAction, Status, TargetProperty, OriginalValue, TamperingAttemptedValue, InitiatingProcessCommandLine, InitiatingProcessAccountName, InitiatingProcessAccountSid, ReportId, DeviceId
| sort by TimeGenerated desc
