properties.query:
// Look within the OfficeActivity table for 'FileDownloaded' operations across multiple SharePoint sites.
let DownloadActivity = OfficeActivity
    | where TimeGenerated >= ago(24h)
    | where Operation == "FileDownloaded"
    // Documents that are uploaded and shared within teams will reside in a 'SourceRelativeUrl' of "Documents/Microsoft Teams Chat Files"
    // These are excluded because they are not the type of data-exfiltration indication that we are looking for.
    | where SourceRelativeUrl != "Documents/Microsoft Teams Chat Files";
DownloadActivity
// Deduplicate Site_Urls (based around the individual accessing) so we can count the unique sites.
| distinct UserId, Site_Url
// Count the number of sites and create a list of them with a maximum number (required of 1000 - which we will probably never hit).
| summarize Sites = make_set(Site_Url, 1000), SiteCount = count() by UserId
// Join the following table to the table we created above.  The join kind is innerunique (see Microsoft Documentation for clarification).
| join kind=innerunique (
    // Look for the same activity but grab the total number of downloaded files.
    DownloadActivity
    // We perform a distinct here and include TimeGenerated because duplication of logs can occur and will affect the count.
    | distinct UserId, OfficeObjectId
    // This will result in a table with UserId and DownloadCount
    | summarize DownloadCount = count() by UserId
    // The tables join on the field 'UserId'
    )
    on UserId
// The threshold we set for SiteCount is 4 (within our time window).  This can change, we have to find a balance of
// data-exfil indication and not working events for normal activity.
| where SiteCount >= 4
| where DownloadCount >= 10
// Create our final table based on the following fields.
| project UserId, DownloadCount, SiteCount, Sites
// Commented out extend below as not needed
//| extend AccountCustomEntity = UserId
// Added join of IdentityInfo to pull user's Department and most current GroupMemberships for comparison to download activity
| join (IdentityInfo
    | where TimeGenerated >= ago(30d)
    | distinct AccountUPN, Department, tostring(GroupMembership))
    on ($left.UserId == $right.AccountUPN)
| summarize GroupMembership = make_list(GroupMembership) by UserId, DownloadCount, SiteCount, tostring(Sites), Department
| project
    UserId,
    Department,
    DownloadCount,
    SiteCount,
    Sites,
    MostCurrentGroupMembership= GroupMembership.[0]
