// Author: Rob Russell
// Description:
// Looks for suspicious webDav client execution. This analytic was built to address MS 0 day related to CVE-2023-23397.
// If the external address contacted is a public IP or non .mil or .gov domain it will fire.
// https://www.huntress.com/blog/everything-we-know-about-cve-2023-23397
// https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_rundll32_webdav_client_susp_execution.yml
// False Positives: No false positives known at this time. It should produce alerts with high condifence. 
let Dav_string = @"rundll32.exe C:\WINDOWS\system32\davclnt.dll,DavSetCookie";
let Http_regex = @"(http:.*)";
let IP_regex = @"([0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3})";
let Domain_regex=@"(rundll32\.exe C:\\WINDOWS\\system32\\davclnt\.dll,DavSetCookie )(([a-z\-0-9\.]+ )(((?P<protocol>[a-z]{2,6})?:\/\/)?)(?P<subdomain>([a-z0-9]{0,10}\.{1}){0,5}(?P<domain>[a-z\-0-9]{1,30}){1})\.{1}(?P<extention>[a-z]{1,10}){1})(?P<path>/.+$)";
let Allowed_TLD = dynamic(['mil','gov']);
let Parse_url = (parse_string:string){extract(Http_regex,0,parse_string, typeof(string))};
let Parse_IP = (parse_string:string){extract(IP_regex,0,parse_string, typeof(string))};
let Parse_domain = (parse_string:string){(extract_all(Domain_regex,dynamic (['domain', 'extention']), parse_string))[0]};
let signInLookup = (
SigninLogs
| where TimeGenerated >= ago(1d)
| where DeviceDetail contains "displayName"
| extend DeviceDetailJson = parse_json(DeviceDetail)
| extend DeviceNameSI = tostring(DeviceDetailJson.displayName)
| distinct DeviceNameSI, UserPrincipalName, UserDisplayName
);
DeviceProcessEvents
| where (ProcessCommandLine startswith Dav_string
  or InitiatingProcessCommandLine startswith Dav_string)
  and (ProcessCommandLine endswith "wav"
  or InitiatingProcessCommandLine endswith "wav")
| extend In_process_cmd = iff(ProcessCommandLine startswith Dav_string, true, false) // checks to see if it is in ProcessCommandLine or InitiatingProcessCommandLine
| extend Url_data = iff(In_process_cmd, Parse_url(ProcessCommandLine), Parse_url(InitiatingProcessCommandLine))
| extend IP_data = iff (In_process_cmd, Parse_IP(ProcessCommandLine),Parse_IP(InitiatingProcessCommandLine))
| extend Is_public_IP = iff(IP_data!="" and not(ipv4_is_private(IP_data)), true, false)
| extend Domain_data = iff (In_process_cmd, Parse_domain(ProcessCommandLine), Parse_domain(InitiatingProcessCommandLine))
| where ((ipv4_is_private(IP_data)==false) and (isnotempty(IP_data))) or
    (not(Domain_data[1] has_any (Allowed_TLD)) and (isnotempty(Domain_data[1]))) // remove not at beginning of line to test against prod data on 7-April 2023
| extend Domain_Contacted = strcat_array(Domain_data, ".")
| extend SILogDeviceName = toupper((split(DeviceName, "."))[0])
| join kind=leftouter signInLookup on $left.SILogDeviceName == $right.DeviceNameSI
| project TimeGenerated, AccountName, UserPrincipalName, DeviceName, IP_Contacted = IP_data , Domain_Contacted, ProcessCommandLine, InitiatingProcessCommandLine

