//Title: BAH_HeadlessBrowserFileDownloads
//Description: Detects execution of chromium-based browsers such as Edge or Chrome in headless mode using the "dump-dom" command line to download files. This threat was detected in March 2023 through Ducktail which threat actors used information-stealing malware to target Facebook business accounts and spear-phishing campaigns through LinkedIn direct messages.
//Author: Steven Wan
//False Positives: None applicable at this time.

let signInLookup = (
SigninLogs
| where TimeGenerated >= ago(1d)
| where DeviceDetail contains "displayName"
| extend DeviceDetailJson = parse_json(DeviceDetail)
| extend DeviceNameSI = tostring(DeviceDetailJson.displayName)
| distinct DeviceNameSI, UserPrincipalName, UserDisplayName
);
DeviceProcessEvents 
| where ((InitiatingProcessFolderPath endswith @"\brave.exe" or InitiatingProcessFolderPath endswith @"\chrome.exe" or InitiatingProcessFolderPath endswith @"\msedge.exe" or InitiatingProcessFolderPath endswith @"\opera.exe" or InitiatingProcessFolderPath endswith @"\vivaldi.exe") and InitiatingProcessCommandLine contains "--headless" and InitiatingProcessCommandLine contains "dump-dom" and InitiatingProcessCommandLine contains "http")
| extend SILogDeviceName = toupper((split(DeviceName, "."))[0]) // for signin lookup by device will find last user signed into device if it was in the last 24 hours
| join kind=leftouter signInLookup on $left.SILogDeviceName == $right.DeviceNameSI // this join will add UserPrincipalName from here forward based on device name
| project TimeGenerated,AccountName,DeviceName,FileName,InitiatingProcessCommandLine,InitiatingProcessFolderPath,InitiatingProcessSHA1
