// Author: Michael Gibbs
// Description: Remote Access and Meeting tools not approved by DISA https://aplits.disa.mil/processAPList.action
// False Positives: If another application uses a similar named DLL or exe
let signInLookup = (
SigninLogs 
| where TimeGenerated >= ago(1d)
| where DeviceDetail contains "displayName"
| extend DeviceDetailJson = parse_json(DeviceDetail)
| extend DeviceNameSI = tostring(DeviceDetailJson.displayName)
| distinct DeviceNameSI, UserPrincipalName
);
let files = dynamic([@"BAConsoleAppEN",
@"takecontroltechconsole",
@"baconsoleapp",
@"GoTo Meeting Opener",
@"GoToOpener.exe",
@"G2MCoreInstExtractor"
]);
(union isfuzzy = true
(
find in (DeviceImageLoadEvents, DeviceEvents, DeviceFileEvents, DeviceImageLoadEvents)
    where FileName has_any (files)
)
    | extend SILogDeviceName = toupper((split(DeviceName, "."))[0])
    | join kind=leftouter signInLookup on $left.SILogDeviceName == $right.DeviceNameSI
    | extend ProcessDetails = pack("FileName", FileName, "FolderPath",FolderPath,"InitiatingProcessCommandLine", InitiatingProcessCommandLine)
    | summarize min(TimeGenerated), max(TimeGenerated), make_set(ProcessDetails) by DeviceName, UserPrincipalName, InitiatingProcessAccountName, MachineGroup
    )
